INSERT INTO `products` VALUES (85,'2024-11-18 13:34:46.528','2024-11-18 14:16:33.803','nho-kho-nguyen-canh',1,1,0,NULL,_binary '','nho-kho','<div class=\"WidgetTitle__WidgetTitleStyled-sc-12sadap-1 bPRVIq\" style=\"font-weight: 600; font-size: 16px; line-height: 24px; color: rgb(39, 39, 42); font-family: Inter, Helvetica, Arial, sans-serif;\"><div class=\"WidgetTitle__WidgetTitleStyled-sc-12sadap-1 bPRVIq\" style=\"line-height: 24px;\"><div class=\"WidgetTitle__WidgetTitleStyled-sc-12sadap-1 bPRVIq\" style=\"line-height: 24px;\">Mô tả sản phẩm</div><div class=\"style__Wrapper-sc-6as7kb-0 eba-dki content\" style=\"display: inline-block; font-size: 14px; font-weight: 400; line-height: 21px; padding-top: 8px; color: rgb(36, 36, 36); text-align: justify; border-radius: 4px;\"><div class=\"ToggleContent__Wrapper-sc-fbuwol-1 xbBes\" style=\"line-height: 21px;\"><div class=\"ToggleContent__View-sc-fbuwol-0 imwRtb\" style=\"position: relative; line-height: 21px;\"><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Trọng lượng: hũ 500gr &amp; hũ 250gr</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Chất lượng: Hàng nhập khẩu Úc chất lượng cao</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Đóng gói: tại Việt Nam</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\"><img src=\"data:image/png;base64,\" data-filename=\"image.png\" style=\"width: 100%;\"></p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">Thành phần: Nho khô nguyên cành Úc 100%</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Bảo quản: Bảo quản nơi khô ráo, thoáng mát, trong ngăn mát tủ lạnh, Đậy kín nắp sau khi sử dụng</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Hạn sử dụng: 12 Tháng kể từ ngày sản xuất, Nên dùng hết trong vòng 3 tháng để giữ được 100% hương vị</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Cách sử dụng: Ăn trực tiếp, Làm nguyên liệu chế biến các món ăn khác. Ăn không hết thì đậy nắp để tủ lạnh ngăn mát để giữ cho hạt luôn giòn như ban đầu</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">️Ngày sản xuất:&nbsp; Chi tiết được in trên bao bì</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">-- -- -- -- -- -- -- -- -- -</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">Nho là món ăn vặt ngon và rất tốt cho sức khỏe. Nho khô nguyên cành được sản xuất theo tiêu chuẩn Úc vẫn giữ nguyên hàm lượng dưỡng chất.</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">- Bổ sung năng lượng cho cơ thể giúp tăng cường sức khỏe hệ miễn dịch.</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">- Tăng cường tiêu hóa : giúp nhuận tràng tốt, ngăn ngừa tình trạng táo bón, ăn không tiêu.</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">- Bảo vệ răng chắc khỏe, giúp ngăn ngừa sâu răng.</p><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\">- Ngăn ngừa loãng xương đặc biệt cho phụ nữ thời kỳ mãn kinh.</p></div></div></div></div><div class=\"style__Wrapper-sc-6as7kb-0 eba-dki content\" style=\"display: inline-block; font-size: 14px; font-weight: 400; line-height: 21px; padding-top: 8px; color: rgb(36, 36, 36); text-align: justify; border-radius: 4px;\"><div class=\"ToggleContent__Wrapper-sc-fbuwol-1 xbBes\" style=\"line-height: 21px;\"><div class=\"ToggleContent__View-sc-fbuwol-0 imwRtb\" style=\"position: relative; line-height: 21px;\"><p style=\"margin: 5px 0px 12px; line-height: 21px; word-break: break-word;\"></p></div></div></div></div>\n													','nho-kho-nguyen-canh.jpg','nho-kho-nguyen-canh-2.jpg','nho-kho-nguyen-canh-3.jpg','nho-kho-nguyen-canh-4.jpg','nho-kho-nguyen-canh-5.jpg','Nho Khô Nguyên Cành Úc',295000,30,320000,'',1,6,2);
INSERT INTO `products` VALUES (79,'2024-10-04 16:52:30.106',NULL,'ca-chua-beef-huu-co',1,0,1,'2024-10-04 17:05:52.068',_binary '','RCQ-Cachua','<span style=\"font-size: 16px;\">Cà chua beef hướng hữu cơ là giống cà chua cao cấp khác hẳn cà chua thông thường ở điểm quả cà chua to, chắc, ít hạt, cơm dày.&nbsp; Cà chua beef cung cấp một lượng Vitamin A, C, K tuyệt vời. Những chất này có tác dụng giúp tăng cường thị lực, phòng bệnh quáng gà. Ngoài ra, cà chua beef hữu cơ còn là loại thực phẩm giúp kiểm soát lượng đường trong máu, có canxi hỗ trợ cho xương chắc khỏe. Cà chua là loại thực phẩm được sử dụng phổ biến trong mỗi bữa ăn cũng như trong làm đẹp của chị em phụ nữ. Tuy nhiên để đảm bảo an toàn thì chúng ta nên chọn cà chua beef hướng hữu cơ hoặc cà chua bi hướng hữu cơ. Thành phần dinh dưỡng của cà chua beef hướng hữu cơ: Cà chua beef là nguồn cung cấp vitamin A, C, K, Các chất carotenoid và bioflavonoid, chất xơ... Quy trình sản xuất cà chua beef hướng hữu cơ: Cà chua beef hướng hữu cơ được trồng bởi trang trại rau organicfood.vn theo phương pháp hữu cơ, đảm bảo không sử dụng thuốc trừ sâu, thuốc kích thích, phân bón hóa học hay bất kì chất độc hại nào.</span>','Ca_Chua_Beef_1.jpg','Ca_Chua_Beef_2.jpg','Ca_Chua_Beef_3.jpg','Ca_Chua_Beef_4.jpg','Ca_Chua_Beef_5.jpg','Cà chua beef hữu cơ - 300g',40000,14,0,'\n														<p><b>Thông tin sản phẩm</b><br></p>\n														<table class=\"table table-bordered\">\n															<tbody>\n																<tr>\n																	<td>Thông tin màn hình:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>CPU:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>RAM:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Camera sau:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Camera trước:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Bộ nhớ trong:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Thẻ sim:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Pin:</td>\n																	<td></td>\n																</tr>\n																<tr>\n																	<td>Hệ điều hành:</td>\n																	<td></td>\n																</tr>\n															</tbody>\n														</table>\n													',32,2,2),(80,'2024-10-04 17:32:51.136',NULL,'cu-den-huu-co',1,0,1,'2024-10-04 19:50:23.635',_binary '','RCQ-Cuden','<p><span style=\"font-size: 16px;\">Có rễ phồng thành củ nạc, ngọt, màu đỏ thẫm. Thân đứng có vằn, ít phân nhánh. Lá có phiến hình trứng, màu lục, có mép lượn sóng. Hoa màu lục nhạt, mọc thành bông khá dài.&nbsp;</span></p><p><img src=\"https://cdn.tgdd.vn/Products/Images/8785/230445/bhx/cu-den-202312281013361985.jpg\" style=\"width: 50%; float: right;\" class=\"note-float-right\"><div style=\"font-size: 16px;\">CÁCH SỬ DỤNG: Củ dền cũng được sử dụng như các loại củ khác để xào hoặc nấu canh. Có thể xào với thịt, nấu canh thịt hoặc hầm với xương; cũng dùng luộc ăn chấm mắm, xì dầu. Ta thường dùng nấu chín ăn, nhưng có thể dùng củ nạo ra ăn trong những đĩa rau sống. Người ta có thể dùng Củ dền xắt nhỏ phơi khô cho đều và nghiền ra thành bột cất dành trong hộp gỗ để dùng cho những người già.</div><div style=\"font-size: 16px;\">LƯỢNG DÙNG: Củ dền có thể được sư dụng một cách linh hoạt theo nhiều cách. Bạn có thể ăn sống, hấp, đun sôi (mặc dù có nhiều chất dinh dưỡng sẽ bị mất đi trong quá trình nấu), hoặc cũng có thể được cho thêm vào món súp hay hầm.</div><div style=\"font-size: 16px;\">CÁCH BẢO QUẢN: Nên chọn những củ còn tươi đa số có vỏ trơn láng, Củ dền tươi giữ được trong hộc đựng trái cây rau củ của tủ lạnh được từ 5 đến 6 ngày và sẽ giữ lâu hơn một chút còn đủ thân, lá.</div>\n													</p>','Cu_den_1.jpg','Cu_den_2.jpg','Cu_den_3.jpg','Cu_den_4.jpg','Cu_den_5.jpg','Củ dền hữu cơ - 500g',90000,14,100000,'',21,2,2),(81,'2024-10-04 19:18:13.517',NULL,'muop-huong-huu-co',1,0,0,NULL,_binary '','RCQ-Muophu','<span style=\"font-size: 16px;\">Mướp hương được thu hoạch từ nông trại chuẩn hữu cơ, không hóa chất.&nbsp; Loại rau này chế biến được thành nhiều món thơm ngon: xào, canh, luộc.....&nbsp;</span><div style=\"font-size: 16px;\"><div>Bổ sung thêm chất xơ vào các bữa ăn hằng ngày.&nbsp;</div><div>Được chứng nhận hữu cơ USDA.</div></div>\n													','Muop_huong_1.jpg','Muop_huong_2.jpg','Muop_huong_3.jpg','Muop_huong_4.jpg','Muop_huong_5.jpg','Mướp hương hữu cơ - 500g',65000,29,70000,'',4,2,2),(82,'2024-10-04 19:25:26.464',NULL,'kho-qua-huu-co',1,0,0,NULL,_binary '','RCQ-Khoqua','<span style=\"font-size: 16px;\">Trên lâm sàng, khổ qua thường dùng chữa các chứng do bệnh nhiệt gây thử nhiệt phiền khát, trúng thử (say nóng), ung sưng, mắt đỏ đau nhức, kiết lỵ, viêm quầng, nhọt độc, tiểu ít… &nbsp;&nbsp;</span><div style=\"font-size: 16px;\">Khổ qua (mướp đắng) – Momordia charantia L. thuộc họ Hồ lô (Cucurbitaceae). Vị đắng, tính mát, không độc. Vào kinh tâm, can, tỳ và vị. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị tăng huyết áp: khổ qua tươi 250g, hành hoa, gừng băm, muối, bột nêm, nước tương (mắm), dầu mè với mỗi thứ vừa đủ. Khổ qua bổ hột, rửa sạch, trụng nước sôi 3 phút, thái sợi, trộn vào hành hoa, gừng băm, muối, bột nêm, nước tương (mắm), dầu mè, trộn đều thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị xơ vữa động mạch: khổ qua tươi 250g, dầu ăn, gừng sợi, hành hoa, muối, bột nêm với mỗi thứ vừa đủ. Khổ qua tươi móc bỏ ruột, rửa sạch, thái sợi, đổ dầu ăn vào chảo, thêm gừng sợi, hành hoa phi thơm, bỏ khổ qua sợi xào nhanh trong giây lát, nêm muối, bột nêm xào sơ thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị cao mỡ máu: khổ qua 1 quả, mật ong 20ml, sữa bò 200ml. Khổ qua bỏ hột, rửa sạch, thái lát hoặc thái nhuyễn, cùng sữa bò xay lấy nước, đổ vào ly, thêm mật ong trộn đều. Mỗi sáng và chiều chia uống 2 lần. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị nhiệt độc tả lỵ: dây khổ qua 60g, đường đỏ vừa đủ. Dây khổ qua rửa sạch, cho vào nồi đất, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã, lấy nước, thêm đường đỏ thì dùng. Ngày 3 – 4 lần. &nbsp; Điều trị vị khí thống: khổ qua vừa đủ rửa sạch, giã nhuyễn, uống với nước ấm. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị cảm cúm: ruột khổ qua vừa đủ rửa sạch, cho vào nồi đất, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã, lấy nước thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị thấp chẩn (chàm): lá khổ qua vừa đủ rửa sạch, giã nhuyễn, đắp tại chỗ. &nbsp; Điều trị trẻ tiêu chảy: dây khổ qua vừa đủ rửa sạch, cho vào nồi đất, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã lấy nước thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị trẻ em kiết lỵ: khổ qua vừa đủ, mật ong vừa đủ. Khổ qua rửa sạch, giã vắt lấy nước, pha với mật ong, ngày 1 – 2 lần. &nbsp; &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị trẻ nôn ói: rễ khổ qua 6g rửa sạch, cho vào nồi đất, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã, lấy nước thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị đại tiện ra máu: rễ khổ qua 200g rửa sạch, cho vào nồi đất, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã lấy nước thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị đinh nhọt đau không chịu được: lá khổ qua rửa sạch, phơi khô, tán mịn, uống với rượu trắng 15g. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị nhọt lâu ngày không vỡ: khổ qua 1 quả rửa sạch, vắt nước, thoa lên nhọt, ngày 3 lần. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị nhiệt độc nhọt sưng: lá khổ qua vừa đủ rửa sạch, giã nhuyễn, vắt nước, thoa tại chỗ. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị tiêu khát (bệnh đái tháo đường): khổ qua 250g rửa sạch, cho vào nồi, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa nhỏ ninh lấy nước cốt, bỏ bã lấy nước thì dùng. Ngày vài lần, mỗi lần 1 chén. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị bệnh nhọt, người cao tuổi bị đái tháo đường biến chứng võng mạc: khổ qua 100g, bắp 100g, đường phèn 10g. Khổ qua và bắp lần lượt rửa sạch, hai thứ cùng cho vào nồi, đổ nước vừa đủ, bắc lên bếp, đun sôi bằng lửa mạnh, chuyển lửa ninh chè, khi chín, nêm đường phèn cho tan đều. Mỗi ngày chia dùng sáng và chiều. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị rết cắn: lá khổ qua 50g, vắt nước, thoa tại chỗ. &nbsp; Điều trị hôi miệng: khổ qua rửa sạch, thái sợi, ướp muối, thêm dầu mè một ít, làm gỏi. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị béo phì thể nhẹ: khổ qua tươi 250g, đậu xị, ớt sợi, đậu tương, dầu ăn, gừng băm, hành hoa, muối, bột nêm với mỗi thứ vừa đủ. Khổ qua móc bỏ ruột, rửa sạch, thái lát mỏng. Đổ dầu vào chảo cho nóng, thêm khổ qua, đậu xị, ớt sợi, đậu tương, hành, gừng băm cùng vào chảo xào sơ, sau cùng nêm muối, bột nêm xào sơ thì dùng. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị viêm gan mạn tính, gan nhiễm mỡ: khổ qua tươi 250g, rau sam tươi 250g, đường trắng 30g. Khổ qua và rau sam lần lượt loại bỏ tạp chất, rửa sạch, mát khô, khổ qua thái lát, rau sam thái nhuyễn, hai thứ cùng xay nhuyễn, cho vào tô, nêm đường trắng trộn đều, sau 2 giờ chắt ra nước cốt. Chia dùng mỗi sáng và chiều.</div><div style=\"font-size: 16px;\"><img src=\"https://cdn.tgdd.vn/Products/Images/8785/291352/bhx/kho-qua-khoang-500g-3-5-trai-202408141431558093.jpg\" style=\"font-size: 1rem;\">&nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị hội chứng mỏi mệt: khổ qua 1 kg, rửa sạch, phơi sấy khô, tán bột, chứa trong lọ hoặc trong túi lọc, mỗi gói 10g, miệng túi đính sợi dây, dán kín miệng. Cho vào ly hãm với nước sôi, ngày 3 lần, mỗi lần 1 gói. &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị sưng tuyến mang tai: khổ qua 1 quả, rong biển, muối, bột nêm, dầu mè với mỗi thứ vừa đủ. Khổ qua móc bỏ ruột, rửa sạch, thái lát, cho vào nồi có nước dùng, đun sôi, vớt váng, sau khi khổ qua nhừ, thêm rong biển, muối, bột nêm, dầu mè thì dùng. &nbsp; &nbsp;&nbsp;</div><div style=\"font-size: 16px;\">Điều trị loãng xương: khổ qua tươi 200g, đậu phụ non 2 lát, hành hoa, gừng băm, muối, bột nêm với mỗi thứ vừa đủ. Khổ qua bỏ hột, rửa sạch, thái lát mỏng, trụng qua nước sôi, vớt ra, đậu phụ cho vào nồi nóng có dầu mè chiên sơ, thêm nước dùng, khổ qua lát, hành hoa, gừng băm, hầm với lửa vừa 10 phút, nêm muối, bột nêm thì dùng.</div>\n													','Kho_qua_1.jpg','Kho_qua_2.jpg','Kho_qua_3.jpg','Kho_qua_4.jpg','Kho_qua_5.jpg','Khổ qua hữu cơ - 300g',45000,29,50000,'',5,2,2),(83,'2024-10-04 19:34:39.496',NULL,'dua-leo-huu-co',1,0,1,'2024-11-20 16:46:47.18',_binary '','RCQ-Dualeo','<p style=\"font-size: 16px;\">Dưa leo hữu cơ được trồng và chăm sóc theo phương pháp hữu cơ, là sự lựa chọn hoàn hảo cho những ai đang tìm kiếm sự tươi ngon và an toàn cho sức khỏe của mình. Với tiêu chuẩn nghiêm ngặt và quy trình sản xuất cẩn thận, dưa leo hữu cơ của chúng tôi đảm bảo mang lại những giá trị dinh dưỡng tốt nhất từ tự nhiên đến tay của quý khách hàng.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Tươi ngon và đầy dinh dưỡng:</span>&nbsp;Dưa leo hữu cơ của chúng tôi được trồng trong môi trường không sử dụng hóa chất độc hại hay phân bón hóa học. Điều này đảm bảo rằng sản phẩm của chúng tôi không chỉ tươi ngon mà còn giàu dinh dưỡng, với hương vị tự nhiên và chất lượng tốt nhất.</p><p style=\"font-size: 16px;\"><img src=\"https://cdn.tgdd.vn/Products/Images/8785/306739/bhx/dua-leo-giong-nhat-cap-2-trai-600g-202305110834574877.jpg\" style=\"width: 50%; float: right;\" class=\"note-float-right\"><br></p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">An toàn cho sức khỏe:</span>&nbsp;Chúng tôi cam kết cung cấp dưa leo hữu cơ không chứa các hóa chất độc hại như thuốc trừ sâu, thuốc diệt cỏ hoặc chất bảo quản nhân tạo. Điều này mang lại sự yên tâm cho quý khách hàng khi sử dụng sản phẩm của chúng tôi, đồng thời đảm bảo an toàn cho sức khỏe của bạn và gia đình.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Hương vị tự nhiên:</span>&nbsp;Dưa leo hữu cơ của chúng tôi được trồng và chăm sóc bằng các phương pháp tự nhiên, giúp giữ lại hương vị đặc trưng và dinh dưỡng tự nhiên của loại rau này. Với khẩu phần dinh dưỡng cân đối và giàu chất chống oxy hóa, dưa leo hữu cơ là lựa chọn lý tưởng để bổ sung vào chế độ ăn hàng ngày.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Hỗ trợ nông dân và môi trường:</span>&nbsp;Bằng cách lựa chọn sản phẩm hữu cơ, bạn không chỉ đảm bảo sức khỏe cho bản thân mình mà còn hỗ trợ cho việc bảo vệ môi trường và hỗ trợ cho nông dân sử dụng phương pháp trồng trọt bền vững. Chúng tôi cam kết hỗ trợ cộng đồng nông dân cùng xây dựng một tương lai bền vững và hài hòa với thiên nhiên.</p><p style=\"margin-bottom: 1rem; font-size: 16px;\">Hãy đặt hàng ngay hôm nay để trải nghiệm sự tươi ngon và an toàn của dưa leo hữu cơ từ Organicfood.vn</p>','Dua_leo_1.jpg','Dua_leo_2.jpg','Dua_leo_3.jpg','Dua_leo_4.jpg','Dua_leo_5.jpg','Dưa leo hữu cơ - 500g',55000,28,60000,'',20,2,2),(84,'2024-10-04 19:42:32.919',NULL,'cu-cai-trang-huu-co',1,0,1,'2024-10-28 17:12:36.533',_binary '','RCQ-Cucait','<p><span style=\"font-size: 16px;\">Củ trắng thuộc họ củ cải, thân tròn dài và to, chứa nhiều nước. Củ cải trắng là một trong những loại củ hỗ trợ điều trị tiểu đường rất hiệu quả.</span></p><p><img src=\"https://cdn.tgdd.vn/Products/Images/8785/233985/bhx/cu-cai-trang-202312251008097587.jpg\" style=\"width: 50%; float: right;\" class=\"note-float-right\"></p><p><span style=\"font-size: 16px;\">CÁCH SỬ DỤNG:&nbsp;</span><span style=\"font-size: 16px;\">Một số món ngon được chế biến từ củ cải trắng như: Canh thịt bò củ cải, củ cải hầm thịt bò, thịt kho củ cải, kim chi củ cải, món chua ngọt...&nbsp;&nbsp;</span></p><p><span style=\"font-size: 16px;\">LƯỢNG DÙNG: Dùng củ cải với lượng vừa đủ sẽ rất tốt cho sức khỏe.</span></p><p><span style=\"font-size: 16px;\">CÁCH BẢO QUẢN: Để củ cải ở nơi mát, ẩm, có thể xếp cạnh các chum, vại nước hoặc buộc kín trong túi nilon, bảo quản trong tủ lạnh vài ngày.</span></p><p><span style=\"font-size: 16px;\">CÔNG DỤNG: Củ cải trắng giúp chữa được rất nhiều bệnh, được lưu truyền rộng rãi trong dân gian. Một số bệnh có thể dùng đến củ cải trắng để chữa trị như: Người bị khàn tiếng không nói được: có thể lấy củ cải trắng và gừng giã lấy nước uống rất hiệu quả. Với một số người bị lao phổi ho ra máu dùng 300g củ cải trắng, 150g mật ong, 10g phèn chua, 400ml nước, cho củ cải vào đun sôi đến khi còn 100ml nước thì gạn lấy nước bỏ bã, sau đó cho mật ong và phèn chua vào khuấy đều, đun sôi rồi chia làm 3 lần uống trước khi ăn. Ngoài ra củ cải còn chữa được các bênh khác như: nhiệt miệng- súc miệng với nước cốt củ cải nhiều lần trong ngày sẽ khỏi, trẻ nhỏ bị ho - lấy củ cải thái thành miếng mỏng, ngâm trong nước đường đặc trong vài ngày, mỗi lần lấy ra một thìa nhỏ, pha với nước nóng cho trẻ uống.</span><span style=\"font-size: 16px;\"></span>\n													</p>','Cu_cai_trang_1.jpg','Cu_cai_trang_2.jpg','Cu_cai_trang_3.jpg','Cu_cai_trang_4.jpg','Cu_cai_trang_5.jpg','Củ cải trắng hữu cơ - 350g',45000,19,0,'',19,2,2),(86,'2024-11-18 15:39:50.672',NULL,'nho-den-khong-hat',1,0,1,'2024-11-20 17:08:37.552',_binary '','nho-den','<p><span style=\"color: rgba(0, 0, 0, 0.8); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, 文泉驛正黑, &quot;WenQuanYi Zen Hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;儷黑 Pro&quot;, &quot;LiHei Pro&quot;, &quot;Heiti TC&quot;, 微軟正黑體, &quot;Microsoft JhengHei UI&quot;, &quot;Microsoft JhengHei&quot;, sans-serif; white-space-collapse: preserve;\">Nho đen không hạt - loại quả giàu dinh dưỡng\n\nTrong số các loại nho có trên thị trường thì nho đen không hạt được xếp vào dạng giàu dưỡng chất nhất. Chúng có thành phần giàu canxi, các loại Vitamin và khoáng chất nbuw B1, B2 và B6 vv. Đặc biệt hàm lượng chất C và các axit amin dồi dào giúp nâng cao sức đề kháng đồng thời có lợi cho hệ tiêu hóa của bạn.</span></p><p><span style=\"color: rgba(0, 0, 0, 0.8); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, 文泉驛正黑, &quot;WenQuanYi Zen Hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;儷黑 Pro&quot;, &quot;LiHei Pro&quot;, &quot;Heiti TC&quot;, 微軟正黑體, &quot;Microsoft JhengHei UI&quot;, &quot;Microsoft JhengHei&quot;, sans-serif; white-space-collapse: preserve;\">\n<div style=\"text-align: center;\"><img src=\"https://cdn.tgdd.vn/Products/Images/8788/234095/bhx/nho-den-khong-hat-nhap-khau-my-1kg-202408241321534405.jpg\" alt=\"Image 5 of 5\" style=\"color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 1rem; width: 50%;\"></div><div style=\"text-align: center;\"><br></div></span><span style=\"color: rgba(0, 0, 0, 0.8); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, 文泉驛正黑, &quot;WenQuanYi Zen Hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;儷黑 Pro&quot;, &quot;LiHei Pro&quot;, &quot;Heiti TC&quot;, 微軟正黑體, &quot;Microsoft JhengHei UI&quot;, &quot;Microsoft JhengHei&quot;, sans-serif; white-space-collapse: preserve;\">Không chỉ có lợi cho sức khỏe mà những quả nho đen không hạt còn có tác dụng dưỡng da làm đẹp rất tốt. Có thể ăn sống để cung cấp vitamin làm đẹp da mà còn có thể làm mặt nạ dưỡng da cực kì hiệu quả. Theo nghiên cứu thì hoạt chất resveratrol trong nho có khả năng chống oxy hóa gấp 7 lần so với vitamin E thông thường nên sẽ làm da của bạn luôn luôn săn chắc hơn. Tình trạng mụn của bạn cũng được cải thiện rất nhiều khi dùng nho mỗi ngày.\n\nCách trồng nho đen không hạt\nNho không hạt không quá khó tuy nhiên do là giống không có hạt nên việc trồng chúng lại khác vì không thể trồng lại từ hạt. Hầu như phương pháp nhân giống chúng đều được trồng từ việc cắt cành của một cây nho lớn theo đường chéo rồi cắt toàn bộ phần nhánh và cành non của cành vừa mới cắt.\n\nTiếp theo cành này sẽ được nhúng phần đàu vào dung dịch hormon kích thích ra rễ. Sau đó chúng sẽ được trồng và chăm sóc thành cây mới\n\nĐất trồng trồng nho tốt nhất              \nNho đen không hạt thích hợp nhất khi trồng trên loại đết phù sa ven sông và những loại đất giàu dinh dưỡng và thoát nước tốt. Độ ph phù hợp nhất cho cây đó chính là khoảng từ 6-7\n\nKhí hậu, ánh sáng, nước tưới\n- Nho là loài cây ưa ánh sáng vì thế cần trồng nho ở nơi thật nhiều ánh sáng giúp quá trình đơm hoa kết trái của Nho được thuận lợi.\n\nCũng giống như những loại nho khác. Nho đen không hạt thuộc loại cây ưa sáng vì thế nên cần trồng nho ở những nơi thật nhiều ánh sáng sẽ giúp cho quá trình đơm hoa kết trái của chúng thuận lợi hơn.\nVề điều kiện khí hậu thì nho đen này ưa khí hậu khô ráo và không có nhiều mưa. Thời kì ra qua đậu quả mà gặp phải lúc mưa nhiều sẽ khiến cho hoa và quả dễ rụng đồng thời sâu bệnh sẽ phát triển mạnh hơn.\n\nÁnh sáng: Nho đen không hạt ưa ánh sáng dồi dào. Khí hậu khô và khá nhạy cảm với lượng mưa nhiều.\n\nLượng nước tưới:\n\nNho nói chung và nho đen không hạt nói riêng có nhu cầu nước khá cao. Chúng tuy không chịu được ngập úng do phần rễ bên dưới cần nhiều oxy để phát triển. Nước tưới nho cần phải được sạch sẽ vì sẽ quyết định đến năng suất và chất lượng nho sau này. Hàng ngày cần tưới chon ho từ 2-3 lần tùy thuộc vào độ ẩm của đất.</span></p>','nho-den-khong-hat-nhap-khau-my.jpg','nho-den-khong-hat-nhap-khau-my-2.jpg','nho-den-khong-hat-nhap-khau-my-3.jpg','nho-den-khong-hat-nhap-khau-my-4.jpg','nho-den-khong-hat-nhap-khau-my-5.jpg','Nho đen không hạt nhập khẩu Mỹ 500g',160000,26,0,'',11,3,2),(87,'2024-11-20 16:17:59.254',NULL,'viet_quat_new_zealand',1,0,1,'2024-11-20 16:58:17.005',_binary '','viet_quat','<p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Xuất xứ:&nbsp;</span>New zealand</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Tiêu chuẩn:&nbsp;</span>Nhập khẩu</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Đặc điểm:</span></p><ul style=\"font-size: 16px;\"><li>Trái siêu to, kích thước xấp xỉ bằng hạt macca, size rất hiếm trên thị trường.&nbsp;</li><li>Cứng trái, cắn vào bụp bụp rất thú vị, bên trong mềm tan cùng vị ngọt thanh và hương thơm đặc trưng chuẩn vị việt quất nhập khẩu chính gốc.&nbsp;</li><li>Theo đánh giá của nhiều chuyên gia, quả việt quất được thu hoạch tại New Zealand cho chất lượng cao và nhiều dưỡng chất nhất.&nbsp;</li><li>Việt quất là một trong những loại quả mọng giàu chất dinh dưỡng nhất. Duy trì thói quen ăn việt quất, chúng sẽ mang đến những lợi ích mà bạn không thể ngờ.</li></ul><div style=\"font-size: 16px;\"><p style=\"text-align: center;\"><img src=\"https://file.hstatic.net/200000423303/file/viet_quat_new_zealand_size_jumbo1_75e1a96f2ba54ba4bff7d64a81c3369e.jpeg\" style=\"max-width: 100%; width: 475px;\"></p><p>&nbsp;</p></div><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Thông tin dinh dưỡng:</span></p><ul style=\"font-size: 16px;\"><li>Quả việt quất giàu các chất chống oxy hóa khác nhau, cùng với các vitamin như vitamin C, B2, B6, E và K, chất xơ, đồng, mangan, lutein, gallic acide,</li><li>Tăng cường chức năng của não: Sự hiện diện của các hợp chất anthocyanin trong quả việt quất rất hữu ích trong việc tăng cường chức năng hoạt động của não, bộ nhớ và chức năng vận động của cơ.</li><li>Giảm cân: Nhờ lượng calo thấp, quả việt quất là một món ăn ngon nếu bạn đang mong muốn giảm cân. Chất xơ trong đó có thể làm tăng cảm giác no, khiến bạn cảm thấy no hơn và ăn ít hơn, đồng thời cho bạn năng lượng để thực hiện các hoạt động thể chất nhằm giảm cân dễ dàng.</li><li>Phòng ngừa các bệnh: Việt quất có nhiều chất chống oxy hoá và chất dinh dưỡng giúp ngăn ngừa đục thủy tinh thể, tăng nhãn áp, ung thư, loét, tiểu đường, viêm khớp.</li><li>Đối với da: Nếu bạn muốn duy trì làn da khỏe mạnh, hãy ăn quả việt quất thường xuyên. Ngoài ra, chúng rất tốt trong việc kiểm soát lão hóa da.&nbsp;</li><li>Tăng cường tiêu hóa: Hàm lượng chất xơ cao trong quả việt quất làm cho chúng trở thành một lựa chọn tuyệt vời để hỗ trợ tiêu hóa khỏe mạnh.</li></ul><div style=\"font-size: 16px;\"><p style=\"text-align: center;\"><img src=\"https://file.hstatic.net/200000423303/file/viet_quat_new_zealand_size_jumbo3_2b1d1840366648e0af7ba8aa2fdad844.jpeg\" style=\"max-width: 100%; width: 475px;\"></p><p>&nbsp;</p></div><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Hướng dẫn sử dụng:</span></p><ul style=\"margin-bottom: 1rem; font-size: 16px;\"><li>Rửa nhẹ nhàng&nbsp;việt quất với nước sạch để loại bỏ phần phấn trắng trên vỏ.</li><li>Giữ lạnh việt quất giúp bảo quản lâu hơn.</li><li>Bạn nên giữ việt quất&nbsp;trong hộp đựng ban đầu và làm lạnh càng sớm càng tốt.</li><li>Không rửa&nbsp;trước khi cho vào tủ lạnh.</li></ul>','viet_quat_new_zealand_1.jpg','viet_quat_new_zealand_2.jpg','viet_quat_new_zealand_3.jpg','viet_quat_new_zealand_4.jpg','viet_quat_new_zealand_5.jpg','Việt quất New Zealand - hộp 125g',150000,27,0,'',12,3,2),(88,'2024-11-20 16:55:06.206',NULL,'luu-thai-lan-huu-co',1,0,0,NULL,_binary '','luu-thai-l','<div class=\"title_module_main heading-bar d-flex justify-content-between align-items-center\" style=\"font-size: 16px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; padding-bottom: 10px; border-bottom: 1px solid rgb(238, 238, 238); padding-top: 5px;\"><h2 class=\"heading-bar__title \" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; font-size: 1.4em; color: var(--text-color);\">Mô tả sản phẩm</h2></div><div class=\"rte product_getcontent py-3 pos-relative\" style=\"font-size: 16px; padding-top: 1rem !important; padding-bottom: 1rem !important;\"><div id=\"content\" style=\"overflow: hidden; word-break: break-word; height: auto;\"><p><span style=\"font-weight: bolder;\">Nguồn gốc:</span></p><p>Lựu Thái Lan là một loại trái cây được trồng tại Thái Lan theo quy trình hoàn toàn hữu cơ, được xuất khẩu đi nhiều nước trên trên thế giới. Cây lựu cần nhiều ánh nắng trực tiếp, ít nhất 8 giờ mỗi ngày, nên mùa vụ của loại lựu đỏ Thái này rơi vào khoảng tháng 9 đến tháng 3 năm sau vì thời điểm này ở Thái là mùa khô, có nhiều nắng. Đây cũng là loại trái cây nhập khẩu được các mẹ săn đón bởi lời truyền “ăn lựu con sẽ có má lúm đồng tiền”.</p><p>Vì là sản phẩm hữu cơ, nên chúng ta hoàn toàn yên tâm khi sử dụng, an toàn và phù hợp cho cả những mẹ bầu và trẻ nhỏ.</p><p><span style=\"font-weight: bolder;\">Mô tả sản phẩm:</span></p><p>Lựu Thái là một loại quả giàu chất chống oxi hóa, lại có hương vị thơm ngon nên rất được yêu thích trên thị trường trái cây nhập khẩu. Lựu Thái có&nbsp;vị ngọt đậm, hạt mềm có thể ăn được, không đắng, mọng nước. Một trái lựu thường có&nbsp;trọng lượng khoảng&nbsp;400 – 600g/ trái, vỏ màu vàng có chổ ửng hồng.</p><p><img src=\"https://product.hstatic.net/200000423303/product/luu_thai_lan_60f714284b714048b30bc219bcb39d39_grande.jpeg\" alt=\"Lựu Thái Lan Hữu Cơ\" style=\"width: 50%; float: right;\" class=\"note-float-right\"></p><p><span style=\"font-weight: bolder;\">Giá trị dinh dưỡng trong một quả lựu:</span></p><p>Lựu Thái chứa nhiều chất dinh dưỡng, đặc biệt là hàm lượng chất xơ cao tốt cho hệ tiêu hóa. Phần vỏ hạt của quả lựu rất ngọt, 100g vỏ hạt lựu cung cấp khoảng 83 kcal và 13,7g đường.</p><p>Các thành phần Natri, Vitamin B2, Niacin, Canxi, Photpho và Vitamin C trong nước ép trái Lựu rất tốt cho thai phụ và sự phát triển trí não của thai nhi, giúp trẻ khi được sinh ra giảm nguy cơ tổn thương ở não và tim mạch.</p><p>Canxi, magie, sắt, phốt pho có trong quả lựu Thái cũng rất tốt cho xương, tránh hiện tượng loãng xương, đồng thời tăng cường chức năng khớp có tác dụng chống viêm, ngăn ngừa các yếu tố gây nên viêm, đau ở các đầu xương khớp.</p><p>Lựu chứa rất nhiều vitamin, nhất là vitamin C và các loại khoáng chất khác. Nước ép lựu có tác dụng hạ cholesterol và có thể làm giảm quá trình lão hóa.</p><p>Lựu chứa nhiều polyphenol có khả năng giảm độ dày thành động mạch, giảm việc hình thành mảng bám và cholesterol xấu, là nhân tố nguy hiểm của bệnh tim.</p><p><span style=\"font-weight: bolder;\">Cách bảo quản lựu Thái:</span></p><p>Bảo quản lựu trong tủ lạnh từ 0-4 độ C, có thể giữ lựu tươi được khoảng 2 tuần.</p><p>Tránh để lựu cùng các thực phẩm có mùi tanh như tỏi, hành.</p><p>Có thể dùng ăn trực tiếp làm món tráng miệng, lựu Thái còn được làm nước ép, sinh tố, chè hay được dùng làm nguyên liệu trong các món ăn như salad, đùi gà sốt lựu thơm ngon, hấp dẫn.</p></div></div>\n													','luu-thai-lan-1.jpg','luu-thai-lan-2.jpg','luu-thai-lan-3.jpg','luu-thai-lan-4.jpg','luu-thai-lan-5.jpg','Lựu Thái Lan Hữu Cơ - 0.5 kg',85000,25,0,'',3,3,2),(89,'2024-11-20 17:22:59.431',NULL,'sau-rieng-chuong-bo',1,0,0,NULL,_binary '','sau-rieng-','<h5 style=\"margin-bottom: 0.5rem; font-size: 2.5rem; color: rgb(0, 0, 0);\"><span style=\"font-weight: bolder;\"><font face=\"Times New Roman\">SẦU RIÊNG CHUỒNG BÒ - SẦU RIÊNG GIỐNG CỔ</font></span></h5><div style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Quy cách: trái ~1.25-1.5kg.</span></div><div style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Vùng canh tác: Việt Nam</span></div><div style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Hướng dẫn bảo quản: bảo quản tủ mát khi sầu chín.</span></div><div style=\"\"><p style=\"font-size: 16px; text-align: center;\"><img src=\"https://file.hstatic.net/200000423303/file/a82a13cc5c4cfd12a45d2_3910c56a7b5d4b148cd2b4f783f2ab77_grande.jpg\" style=\"max-width: 100%; height: auto !important;\"></p><div style=\"text-align: center; \">Sầu riêng chuồng bò 1.5kg/trái</div><br><span style=\"font-size: 16px;\">Sầu riêng chuồng bò thuộc giống sầu riêng có kích thước nhỏ với trọng lượng chỉ khoảng 1 - 2.5kg. Quả có dáng hình trụ, hơi bầu bầu với phần gai to và dày. Sầu riêng chuồng bò khi chín sẽ có vỏ màu vàng xám.&nbsp;</span><br><span style=\"font-size: 16px;\">Phần cơm sầu riêng chuồng bò có màu vàng nhàn nhạt, cơm mềm và hơi nhão. Khi ăn sẽ cảm thấy vị ngọt nhẹ không quá ngọt gắt và có vị beo béo hơn hẳn so với các giống sầu riêng khác.&nbsp;</span><br><span style=\"font-size: 16px;\">Sầu không sử dụng hoá chất trong quá trình canh tác, không nhúng chất kích chín rất an toàn cho sức khoẻ.</span></div><div style=\"\"><span style=\"font-size: 16px;\"><br></span></div><div style=\"font-size: 16px;\"><h2 style=\"margin-bottom: 0.5rem; font-size: 2rem;\"><span style=\"font-weight: bolder;\"><font face=\"Times New Roman\">Cách chọn sầu riêng chuồng bò NGON</font></span></h2><p>Để tránh mua nhầm các giống sầu riêng khác, bạn nên nắm rõ hình dáng bên ngoài của sầu riêng chuồng bò, chẳng hạn: trái có kích thức nhỏ khoảng từ 1 - 2.5kg, có dánh hình trụ, hơi bầu bầu. Khi bổ ra, cơm sầu có màu vàng nhạt, mềm và hơi nhão.</p><p style=\"text-align: center;\"><img src=\"https://file.hstatic.net/200000423303/file/ruot-sau-rieng-chuong-bo_a547b79982214eafaac96a8fd5d15520_grande.jpg\" style=\"max-width: 100%; height: auto !important;\"></p><p style=\"text-align: center;\">Ruột sầu riêng chuồng bò vàng nhạt</p><p><br>Sầu riêng ngon khi chín sẽ có hương thơm đậm đà, đặc trưng, còn sầu riêng ngâm thuốc sẽ không thơm và đôi khi còn có mùi khiến mũi bạn sẽ cảm thấy hơi khó chịu.<br>Bạn nên chọn mua những trái có cuống màu xanh, dính chặt vào quả, dùng tay bấm vào phần cuống sẽ thấy có nhựa ướt.<br>Sầu riêng chuồng bò thường tươi ngon vào mùa sau tháng 5 Âm lịch, vì lúc đó là đúng mùa vụ sầu riêng chín dần và thơm ngon nhất.</p><h2 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 2rem;\"><span style=\"font-weight: bolder;\"><font face=\"Times New Roman\">Sầu riêng chuồng bò giá bao nhiêu?</font></span></h2><p style=\"text-align: center;\"><img src=\"https://file.hstatic.net/200000423303/file/com-sau-rieng-tach-san_57dbe1e47fdc4c5d932166a6f178d5f6_grande.jpg\" style=\"max-width: 100%; height: auto !important;\"></p><p style=\"text-align: center;\">Sầu riêng chuồng bò tách sẵn</p><p><br>Hiện nay, sầu riêng chuông bò được bán tại hệ thống cửa hàng Organicfood.vn vs giá 275.000/kg, ngoài ra bạn&nbsp;có thể lựa chọn sầu riêng&nbsp;tách sẵn tại Organicfood.vn giá 690.000/kg.&nbsp;</p></div>\n													','sau-rieng-chuong-bo-1.jpg','sau-rieng-chuong-bo-2.jpg','sau-rieng-chuong-bo-3.jpg','sau-rieng-chuong-bo-4.jpg','sau-rieng-chuong-bo-5.jpg','Sầu Riêng Chuồng Bò 1kg',260000,27,0,'',3,3,2),(90,'2024-11-21 18:52:43.748',NULL,'tao-new-zealand',1,0,1,'2024-11-21 19:07:01.734',_binary '','taonewzeal','<h3 style=\"margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">1. Giới thiệu chung về táo Queen hữu cơ New Zealand</h3><p style=\"font-size: 16px;\">Táo Queen hữu cơ từ New Zealand là một loại trái cây cao cấp, nổi bật với vị ngọt thanh, giòn tan và hương thơm tự nhiên đặc trưng. Với nguồn gốc từ những trang trại hữu cơ hàng đầu tại New Zealand, táo Queen không chỉ ngon mà còn an toàn cho sức khỏe người tiêu dùng. Được chăm sóc kỹ lưỡng và thu hoạch theo tiêu chuẩn nghiêm ngặt, loại táo này ngày càng chiếm được lòng tin của người tiêu dùng trên toàn thế giới.</p><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">2. Nguồn gốc và quá trình canh tác</h3><p style=\"font-size: 16px;\">Táo Queen hữu cơ New Zealand được trồng tại các trang trại hữu cơ đạt chuẩn quốc tế. Các trang trại này tuân thủ nghiêm ngặt các quy trình canh tác hữu cơ, không sử dụng hóa chất hay thuốc trừ sâu tổng hợp. Thay vào đó, nông dân áp dụng các biện pháp canh tác tự nhiên, giúp duy trì độ phì nhiêu của đất và bảo vệ môi trường sinh thái.</p><p style=\"font-size: 16px;\">Từ giai đoạn ươm mầm đến khi cây trưởng thành, quá trình canh tác táo Queen luôn được giám sát chặt chẽ. Điều này đảm bảo rằng mỗi quả táo đều đạt chất lượng cao nhất, giữ nguyên vẹn các giá trị dinh dưỡng và an toàn cho sức khỏe.</p><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">3. Đặc điểm nổi bật của táo Queen hữu cơ</h3><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Vị ngọt thanh và giòn tan:</span>&nbsp;Một trong những lý do khiến táo Queen được ưa chuộng là hương vị ngọt thanh, không quá gắt, kết hợp cùng độ giòn đặc trưng. Khi cắn vào, bạn sẽ cảm nhận được sự tươi mới, sảng khoái mà ít loại táo nào có thể sánh kịp.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Màu sắc hấp dẫn:</span>&nbsp;Táo Queen có lớp vỏ màu đỏ đậm pha lẫn chút sắc vàng, tạo nên vẻ ngoài rực rỡ và bắt mắt. Đây cũng là một trong những đặc điểm giúp táo Queen trở thành sự lựa chọn hàng đầu cho những người yêu thích hoa quả.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Hương thơm tự nhiên:</span>&nbsp;Táo Queen không chỉ ngon mà còn có hương thơm tự nhiên rất dễ chịu. Hương thơm này giúp kích thích vị giác và làm tăng cảm giác ngon miệng.</p><p style=\"text-align: center; font-size: 16px;\"><img src=\"https://product.hstatic.net/200000423303/product/294753037_1463005067445730_5283082457507312655_n_ce8bef63ae7e4d81aa06ee6f05cce68f_grande.jpg\" alt=\"Táo Queen Organic New Zealand 1kg\" style=\"width: 50%;\"></p><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">4. Lợi ích sức khỏe khi sử dụng táo Queen hữu cơ</h3><p style=\"font-size: 16px;\">Táo Queen hữu cơ chứa nhiều dưỡng chất thiết yếu, giúp cải thiện sức khỏe và tăng cường hệ miễn dịch.</p><ul style=\"font-size: 16px;\"><li><p><span style=\"font-weight: bolder;\">Chứa nhiều chất xơ:</span>&nbsp;Táo Queen là nguồn cung cấp chất xơ tự nhiên, giúp cải thiện hệ tiêu hóa và ngăn ngừa táo bón. Việc tiêu thụ táo Queen hàng ngày còn giúp duy trì cân nặng lý tưởng và giảm nguy cơ mắc các bệnh về tim mạch.</p></li><li><p><span style=\"font-weight: bolder;\">Giàu vitamin và khoáng chất:</span>&nbsp;Táo Queen chứa nhiều vitamin C, vitamin A, và các chất chống oxy hóa. Những dưỡng chất này giúp bảo vệ tế bào khỏi sự tấn công của các gốc tự do, ngăn ngừa lão hóa và hỗ trợ sức khỏe tổng quát.</p></li><li><p><span style=\"font-weight: bolder;\">Ít calo:</span>&nbsp;Táo Queen là một loại thực phẩm ít calo, thích hợp cho những ai đang ăn kiêng hoặc muốn duy trì cân nặng ổn định. Một quả táo Queen trung bình chỉ chứa khoảng 80-100 calo, đủ để cung cấp năng lượng mà không lo bị tăng cân.</p></li></ul><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">5. Cách lựa chọn và bảo quản táo Queen hữu cơ</h3><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Lựa chọn:</span>&nbsp;Khi mua táo Queen, hãy chọn những quả có vỏ mịn, không bị trầy xước hay có vết đốm. Quả táo nên cứng cáp, không bị mềm nhũn hay có dấu hiệu bị héo.</p><p style=\"font-size: 16px;\"><span style=\"font-weight: bolder;\">Bảo quản:</span>&nbsp;Táo Queen nên được bảo quản trong tủ lạnh ở nhiệt độ từ 0 đến 4 độ C để giữ được độ tươi ngon lâu nhất. Nếu muốn bảo quản lâu dài hơn, bạn có thể đặt táo trong túi nhựa kín để ngăn chặn sự bay hơi nước.</p><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">6. Sử dụng táo Queen trong ẩm thực</h3><p style=\"font-size: 16px;\">Táo Queen không chỉ là món trái cây ăn liền tuyệt vời mà còn có thể được sử dụng trong nhiều món ăn khác nhau:</p><ul style=\"font-size: 16px;\"><li><p><span style=\"font-weight: bolder;\">Salad:</span>&nbsp;Thêm vài lát táo Queen vào món salad sẽ làm tăng hương vị và độ giòn, mang lại một trải nghiệm ẩm thực mới lạ.</p></li><li><p><span style=\"font-weight: bolder;\">Sinh tố:</span>&nbsp;Táo Queen có thể được xay nhuyễn để làm sinh tố, kết hợp cùng các loại hoa quả khác như chuối, dâu tây hay cam, tạo nên một thức uống bổ dưỡng và thơm ngon.</p></li><li><p><span style=\"font-weight: bolder;\">Bánh táo:</span>&nbsp;Táo Queen là nguyên liệu lý tưởng cho các món bánh ngọt như bánh táo nướng, tart táo hay bánh mì táo quế.</p></li></ul><h3 style=\"margin-top: 0px; margin-bottom: 0.5rem; font-size: 1.75rem; color: rgb(0, 0, 0);\">7. Mua táo Queen hữu cơ New Zealand ở đâu?</h3><p style=\"margin-bottom: 1rem; font-size: 16px;\">Táo Queen hữu cơ New Zealand hiện có mặt tại nhiều cửa hàng và siêu thị lớn trên toàn quốc. Bạn có thể tìm mua loại táo này tại các&nbsp;<a href=\"http://organicfood.vn/\" style=\"color: var(--text-color); box-shadow: none; outline: none !important;\">cửa hàng chuyên cung cấp thực phẩm hữu cơ</a>, hoặc đặt hàng online để được giao hàng tận nhà.</p>\n													','tao-newzealand-1.jpg','tao-newzealand-2.jpg','tao-newzealand-3.jpg','tao-newzealand-4.jpg','tao-newzealand-5.jpg','Táo Queen Organic New Zealand 1kg',158000,28,0,'',1,3,2),(91,'2024-11-21 18:58:45.399',NULL,'tao-xanh-ninh-thuan',1,0,1,'2024-11-21 19:03:14.414',_binary '','taoxanhnin','<div class=\"block-text\" style=\"margin: auto; padding: 0px; line-height: 1; color: rgb(7, 11, 17); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16.5px; text-align: justify;\"><h2 id=\"menu-1\" style=\"margin: 10px 0px 7px; padding: 0px; text-align: left;\"><span class=\"dropcap\" style=\"margin: 0px; padding: 10px 10px 10px 0px; line-height: 50px; font-size: 75px; float: left; color: rgb(0, 153, 204);\"><span style=\"color: rgb(55, 71, 79); font-size: 2.143rem;\">1. Giới thiệu đôi nét về&nbsp;Táo xanh Phan Rang</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 7px 0px; line-height: 1.5; clear: both;\"></p><p style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 7px 0px; line-height: 1.5;\">Táo xanh Phan Rang hay còn có tên gọi khác là táo ta, táo ga